/* è§¦æ‘¸ç›®æ ‡ä¼˜åŒ– */
.clickable {
  min-height: 44px;
  min-width: 44px;
  padding: 12px 16px;
  cursor: pointer;
  transition: all 0.2s ease;
}

.clickable:hover {
  background-color: rgba(0, 0, 0, 0.05);
  transform: translateY(-1px);
}

.clickable:active {
  background-color: rgba(0, 0, 0, 0.1);
  transform: translateY(0);
}

.clickable:focus {
  outline: 2px solid #007bff;
  outline-offset: 2px;
}

/* æŒ‰é’®çŠ¶æ€ä¼˜åŒ– */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-height: 44px;
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
}

.btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  pointer-events: none;
}

.btn-primary {
  background-color: #007bff;
  color: white;
}

.btn-primary:hover:not(:disabled) {
  background-color: #0056b3;
}

/* é“¾æ¥ä¼˜åŒ– */
a {
  color: #007bff;
  text-decoration: underline;
  text-decoration-thickness: 2px;
  text-underline-offset: 4px;
  transition: all 0.2s ease;
}

a:hover {
  color: #0056b3;
  text-decoration-thickness: 3px;
}

